(()=>{(function(){function e(t){return t<10?"0"+t:t}function o(){let t=document.getElementById("runningtime");if(!t)return;let r=t.dataset.startDate;if(!r)return;let s=new Date(r),n=new Date-s,i=Math.floor(n/(1e3*60*60*24)),c=Math.floor(n/(1e3*60*60)%24),a=Math.floor(n/(1e3*60)%60),u=Math.floor(n/1e3%60),m=`${i} \u5929 ${e(c)} : ${e(a)} : ${e(u)}`;t.textContent=m}document.addEventListener("DOMContentLoaded",()=>{o(),setInterval(o,1e3)})})();})();
